package com.syntax.class07;

public class LoopMore {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num=1;
while(num<=5) {System.out.println("HELLO");
num++;
}
System.out.println("==================");

int num1=1;
do {System.out.println("hello"); num1++;
	} while(num1<=5);}

}
